import React, {useState} from "react";
import Header from "./Header";
import Footer from "./Footer";
import Note from "./Note";

import CreateArea from "./CreateArea";


function App () {



const [newItem, setNewItem] =useState([
    {
        key: 1,
        title: "Delegation",
        content:
          "Q. How many programmers does it take to change a light bulb? A. None – It’s a hardware problem"
      },
      {
        key: 2,
        title: "ation",
        content:
          "programmers does it take to change a light bulb? A. None – It’s a hardware problem"
      }
]);

      function addNote(notetitle,notecontent){

setNewItem([...newItem,{
    
    title:notetitle,
content:notecontent
}])
      }


      function supprimeNote(){

        console.log("hello");
    
    }

    return(
        <div>
        <Header/>
        <CreateArea 
            addNote={addNote}
        />
        {newItem.map(noteItem => 
        <Note
        key={noteItem.key}
        title={noteItem.title}
        content={noteItem.content}
        deleteNote={supprimeNote}
        />



)}
        <Footer/>
        </div>
    );
    
}

export default App;




