import React, {useState} from "react";

function CreateArea() {

const [inputText, setInputText] = useState({
title:"",
content:""

}); 

function inputChange (e){
    const {name,value} = e.target
   if (name==="title") {
    setInputText({
        title:value});
   }else {

   }
    setInputText(value);
}

  return (
    <div>
      <form>
        <input onChange={inputChange} name="title" placeholder="Title" />
        <textarea onChange={inputChange} name="content" placeholder="Take a note..." rows="3" />
        <button>Add</button>
      </form>
    </div>
  );
}

export default CreateArea;
